mapper.select:
SELECT COUNT(*)
FROM search
WHERE sword=#{sword}

mapper.update:
UPDATE search
SET scnt = scnt+1
WHERE sword = #{sword}

UPDATE post
SET ctcode=#{ctcode}, pstatus=#{pstatus}, pprice=#{pprice}, pimg1=#{pimg1}, pimg2=#{pimg2}, pimg3=#{pimg3}, pimg4=#{pimg4}, pimg5=#{pimg5}, ptitle=#{ptitle}, pcontent=#{pcontent}
WHERE bid = 2
AND pnum=#{pnum}
